$(document).ready(function() {
    $(".show_navigation").click(function() {
        $(".nav-main").css({
            position: "fixed",
            width: "100%",
            margin: "0 auto",
            background: "#fff",
            "border-bottom": "1px dotted #333"
        });
        if ($(window).width() <= 1e3) {
            $("body").css({
                "margin-top": "253px"
            })
        } else {
            $("body").css({
                "margin-top": "199px"
            })
        }
        $(".show_navigation").css({
            display: "none"
        });
        $(".close_navigation").css({
            display: "block"
        })
    });
    $(".close_navigation").click(function() {
        $(".nav-main").css({
            position: "relative",
            width: "100%",
            background: "#fff",
            "border-bottom": "none"
        });
        $("body").css({
            "margin-top": "0px"
        });
        $(".close_navigation").css({
            display: "none"
        });
        $(".show_navigation").css({
            display: "block"
        })
    })
});
